mapper.insert:
INSERT INTO ITEMS
(
  ITEM_NM
, ITEM_MAIN_CTGR_CD
, ITEM_MID_CTGR_CD
, ORIGIN_CD
, ITEM_DESC
, NOTICE
, KEEPING_METHOD
, RECOMMEND_YN
, USE_YN
, SHIP_YN
, SOLD_OUT_YN
, DEL_YN
, SORT_ORDER
, DAMAGE_REMARKS
)
VALUES
(
  #{itemNm}
, #{itemMainCtgrCd}
, #{itemMidCtgrCd}
, #{originCd}
, #{itemDesc}
, #{notice}
, #{keepingMethod}
, #{recommendYn}
, #{useYn}
, #{shipYn}
, #{soldOutYn}
, 'N'
, ${sortOrder}
, #{damageRemarks}
)

mapper.update:
UPDATE ITEMS
   SET ITEM_NM = #{itemNm}
      , ITEM_MAIN_CTGR_CD = #{itemMainCtgrCd}
      , ITEM_MID_CTGR_CD = #{itemMidCtgrCd}
      , ORIGIN_CD = #{originCd}
      , ITEM_DESC = #{itemDesc}
      , NOTICE = #{notice}
      , KEEPING_METHOD = #{keepingMethod}
      , RECOMMEND_YN = #{recommendYn}
      , USE_YN = #{useYn}
      , SHIP_YN = #{shipYn}
      , SOLD_OUT_YN = #{soldOutYn}
      , SORT_ORDER = #{sortOrder}
      , DAMAGE_REMARKS = #{damageRemarks}
 WHERE ITEM_NO = ${itemNo}

UPDATE ITEMS
   SET DEL_YN = 'Y'
 WHERE ITEM_NO = ${itemNo}

mapper.insert:
INSERT INTO ITEM_OPTION
(
  ITEM_NO
, OPTION_NM
, ITEM_PRICE
, SHIPPING_FEE
, USE_YN
, DEL_YN
, SORT_ORDER
)
VALUES
(
  ${itemNo}
, #{optionNm}
, ${itemPrice}
, ${shippingFee}
, #{useYn}
, 'N'
, ${sortOrder}
)

mapper.update:
UPDATE ITEM_OPTION
   SET OPTION_NM = #{optionNm}
      , ITEM_PRICE = ${itemPrice}
      , SHIPPING_FEE = ${shippingFee}
      , USE_YN = #{useYn}
      , SORT_ORDER = ${sortOrder}
 WHERE ITEM_NO = ${itemNo}
   AND OPTION_NO = ${optionNo}

mapper.delete:
UPDATE ITEM_OPTION
   SET DEL_YN = 'Y'
 WHERE ITEM_NO = ${itemNo}
   AND OPTION_NO = ${optionNo}
